package com.class7;

public class HW {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
//sum of odd and even numbers between 1 to 50
		
	int sumOdd=0;
	int sumEven=0;
	for(int i=1;i<=50;i+=2) {
		sumOdd+=i;
	}System.out.println(sumOdd);
		
	for(int i1=0;i1<=50;i1+=2) {
		sumEven+=i1;
	}	System.out.println(sumEven);
		
	
		
		
		
		
		
		
		
		
		
		
		
	}

}
